import React, { Component } from 'react';
import { withRouter } from "react-router-dom";

import SocketServer from '../../utils/socket-Server.js';
import HttpServer from '../../utils/http-to server.js';

import './index.css';

class Index extends Component {
  userId = null;
  state = {
    isOverlayVisible: true,
    username: '______',
    selectedUser: null
  }

  logout = async () => {
    try {
      await HttpServer.removeLS();
      SocketServer.logout({
        userId: this.userId
      });
      SocketServer.eventEmitter.on('logout-response', (loggedOut) => {
        this.props.history.push(`/`);
      });
    } catch (error) {
      console.log(error);
      alert(' This App is Broken, we are working on it. try after some time.');
      throw error;
    }
  }

  setRenderLoadingState = (loadingState) => {
    this.setState({
      isOverlayVisible: loadingState
    });
  }

 


  async componentDidMount() {
    try {
      this.setRenderLoadingState(true);
      this.userId = await HttpServer.getUserId();
      const response = await HttpServer.userSessionCheck(this.userId);
      if (response.error) {
        this.props.history.push(`/`)
      } else {
        this.setState({
          username: response.username
        });
        HttpServer.setLS('username', response.username);
        SocketServer.establishSocketConnection(this.userId);
      }
      this.setRenderLoadingState(false);
    } catch (error) {
      this.setRenderLoadingState(false);
      this.props.history.push(`/`)
    }
  }

  updateSelectedUser = (user) => {
    this.setState({
      selectedUser: user
    });
  }

  render() {
    return (
      <div className="App">
        <div className = {`${this.state.isOverlayVisible ? 'overlay': 'visibility-hidden' } `}>
          <h1>Loading</h1>
        </div>
        <header className="app-header">
          <nav className="navbar navbar-expand-md">
            <h4>Hello {this.state.username} </h4>
          </nav>
          <ul className="nav justify-content-end">
            <li className="nav-item">
              <button type="button" className="nav-link" href="#" onClick={this.logout}>Logout</button>
            </li>
          </ul>
        </header>

        <main role="main" className="container content" >
          <div className="row quote-content">
            
          </div>
        </main>
      </div>
    );
  }
}

export default withRouter(Index)
